import React, { useState } from 'react';
import './App.css';
import Overview from './categories/Overview';

const categories = [
  "Overview", "Air quality", "Water quality", "Biodiversity",
  "Public Health", "Energy", "Waste Management", "Forests",
  "Oceans", "Land Use"
];

function App() {
  const [activeCategory, setActiveCategory] = useState('Overview');

  const renderContent = () => {
    switch(activeCategory) {
      case 'Overview':
        return <Overview />;
      // We'll add other cases as we create more components
      default:
        return <div>Content for {activeCategory} coming soon!</div>;
    }
  };

  return (
    <div className="App">
      <h1>EcoDash: Environment and Biodiversity Dashboard</h1>
      <div className="category-buttons">
        {categories.map(category => (
          <button 
            key={category}
            onClick={() => setActiveCategory(category)}
            className={activeCategory === category ? 'active' : ''}
          >
            {category}
          </button>
        ))}
      </div>
      <div className="content">
        {renderContent()}
      </div>
    </div>
  );
}

export default App;
